void mainImage( out vec4 O, in vec2 uv1 ) {
	uv1 /= iResolution.xy;
    vec2 k = floor(7. * uv1.yx);
    vec2 uv2 = floor(k*uv1 + .5) / k;
    float a = texture(iChannel0, vec2(uv1.x, uv2.y)).r;
    float b = texture(iChannel0, vec2(uv2.x, uv1.y)).r;
	O = vec4(1., 1., 1., 1.);
    if(a > .5) {
        O -= vec4(.2*uv1.x, .6, 8., 0.);
    }
    if(b > .5) {
        O -= vec4(.8, .4, .2*uv1.y, 0.);
    }
    O *= 1.-pow(length(uv1-vec2(.5,.5)), 5.);
}
