#define EPS 0.002
#define STEPS 1028
#define FAR 100.0
#define PI acos( -1.0 )

mat2 rot( float a )
{

    return mat2( cos( a ), -sin( a ),
                 sin( a ),  cos( a )
               );

}

float modY( vec3 p, float siz )
{

    float hal = siz * 0.5;
    return mod( p.y , siz ) - hal;

}

float rand( float h )
{

    return fract( sin( h * 78.43 ) * 6483.933 );

}

float sdCappedCylinder( vec3 p, vec2 h )
{
    vec2 d = abs(vec2(length(p.xz),p.y)) - h;
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float map( vec3 p )
{

	// set size of domain
    float tileSize = 1.0;
	// get ID for each domain
    vec3 id=vec3(int(p.x/tileSize),int(p.y/tileSize),int(p.z/tileSize));
	// domain repition

    p = mod(p,tileSize)-tileSize*0.5;
	// rotation, based on ID    
    p.xy *= rot( id.x + id.y + id.z + iTime);
    p.zy *= rot( id.x + id.y + id.z + iTime);
    //p.x += iTime;

    return sdCappedCylinder( p, vec2( 0.1, 0.2 ) );


}

vec3 norm( vec3 p )
{

    vec2 e = vec2( EPS, 0.0 );
    return normalize( vec3( map( p + e.xyy ) - map( p - e.xyy ),
                            map( p + e.yxy ) - map( p - e.yxy ),
                            map( p + e.yyx ) - map( p - e.yyx )
                          ) );

}

float ray( vec3 ro, vec3 rd, out float d )
{

    float t = 0.0;
    for( int i = 0; i < STEPS; ++i )
    {

        d = 0.5 * map( ro + rd * t );
        if( d < EPS || t > FAR ) break;
        t += d;

    }

    return t;

}

vec3 shad( vec3 ro, vec3 rd )
{

    float d = 0.0;
    float t = ray( ro, rd, d );
    vec3 p = ro + rd * t;
    vec3 n = norm( p );
    return n;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( -iResolution.xy + 2.0 * fragCoord ) / iResolution.y;

    float siz = 4.0;

    float x = sin( iTime ) * 0.2;

    //uv *= rot( fract( sin( id.x ) * 78.5 ) * 399.0 ); 

    //uv += vec2( 0.0, x );

    //uv.y += fract( sin( id.x * 758.4 ) * 683.9 ); 
    //uv += fract( uv ) - 0.5;

    vec2 mou = iMouse.xy / iResolution.xy;

    vec3 ro = 0.5 * vec3( sin( mou.x * PI ), cos( mou.y ), cos( -mou.x * PI ) );


    //vec3 rd = normalize( vec3( uv, -1.0 ) );
    vec3 ww = normalize( vec3( 0.0 ) - ro );
    vec3 uu = normalize( cross( vec3( 0.0, 1.0, 0.0 ), ww ) );
    vec3 vv = normalize( cross( ww, uu ) );
    vec3 rd = normalize( uv.x * uu + uv.y * vv + 1.5 * ww );


    float d = 0.0;
    float t = ray( ro, rd, d );

    // Time varying pixel color
    vec3 col = d < EPS ? shad( ro, rd ) : vec3( 0.0 );

    float m = 0.0;

    if( uv.x > 0.46 || uv.y > 0.46 ) m = 1.0;

    // Output to screen
    fragColor = vec4(col,1.0);
}
