//todo: make spectrum take inputs from 0-1
#define PI4 12.566368

vec3 spectrum(float x) {
    x = x * 2.1 - 0.555;
    vec4 v = vec4(clamp(x, -.6, 0.6),
                  clamp(x, 0.05, 1.05),
                  clamp(x, 0.65, 1.55),
                  clamp(x, 1.16, 1.55));
    v += vec4(0.0, -0.55, -1.1, -1.35);
    v *= vec4(0.8, 1.0, 1.1, 2.5);
    v = (cos(v * v * PI4) * 0.5 + 0.5);
    v.r += v.a * 0.25;
    return v.rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    if (uv.y > 0.5)
 	    fragColor = vec4(uv.xxx, 1.0);
    else
        fragColor = vec4(spectrum(uv.x).rgb, 1.0);

}
