void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float aspect = iResolution.y /iResolution.x;
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= aspect;
    vec2 center = vec2(0.5 + 0.4 * sin(iTime), 
                       0.5 + 0.4 * cos(iTime*1.18));
    vec2 c2 = vec2(0.5 + 0.4 * sin(iTime * 1.37), 
                       0.3 + 0.2 * cos(iTime*1.0));
    float cdist = length(center-c2);
    float dist_1 = length(uv - center);
    float dist_2 = length(uv - c2);
    float tdist = dist_1 + dist_2 - length(vec2(dist_1, dist_2));
    float level = floor(fract((tdist) * 40.0) * 20.0);;
    fragColor = vec4(level, level, level, 1.0);
}
