float sceil(float n)
{   
    n = pow((fract(n)*2.-1.),9.)/2.+ceil(n);
 	return n;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy-iResolution.xy) / iResolution.y;

    float layer = 5.-ceil(length(uv)*8.)*.3;

	fragColor = vec4(smoothstep(-.1,.1, cos(atan(uv.x,uv.y)*4.+iTime*layer)));
}
