#define blur
#define c1 vec4(1.,1.,1.,1.)
#define c2 vec4(0.,0.13,0.,1.)
#define aa 4.
#define samples 0.6


#define speed max(1.1,iMouse.x/iResolution.x*20.)
#define time iTime*speed
#define p(t) vec2(sin(t),cos(t)/2.)

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord.xy / iResolution.xy*2.-1.;
    uv.x*=iResolution.x/iResolution.y;
    fragColor=c2;
    #ifdef blur
    for(float i=-speed*5.*samples+1.;i<speed*5.*samples;i++){
        vec2 c=p(time-i/350./samples);
        float d=distance(c,uv);
        if(d<0.1){
            fragColor = mix(c1,c2,abs(i)/(speed*4.5*samples)/1.1);
            if(i>0.)break;
        }
    }
    #else
    fragColor = mix(c2,c1,smoothstep(0.1,0.1-aa/iResolution.y,distance(p(time),uv)));
    #endif
}
