// 247 chars - color shading and centered
// Fabrice has already made this smaller ...
/**/
float m(vec3 p)
{
    float i=0., s = 1., k;
    for(p.y += .2*iTime ; i++<7.; s *= k )
        p *= k = 1.5 / dot(p = mod(--p, 2.) - 1., p);
    return length(p)/s - .01;   
}

#define mainImage(c,u)	                 \
    vec3 o = iResolution,                \
         d = vec3((u+u-o.xy)/o.y, 1)/6.; \
         o/=o;	                         \
    while(c.w++<1e2) o += m(o)*d;        \
    c.rgb = ( o*m(o - d) - 2. ) / o.z + 1.
