float iter(vec2 z, vec2 c) {
    float n = 0.0;
    float B = 256.0;

    for (int i = 0; i < 200; i++) {
     	z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;
        if (dot(z,z) > B*B)
            break;
        n += 1.0;
    }

    // http://www.iquilezles.org/www/articles/mset_smooth/mset_smooth.htm
	return n - log2(log2(dot(z,z))) + 4.0;
}

float mandel(vec2 p, vec2 o) {
    vec2 c = vec2(-2.5, -1.0) + 2.0*p;
    vec2 z = 0.5*o;

	return iter(z, c);
}

float julia(vec2 p, vec2 o) {
    float ratio = iResolution.x/iResolution.y;
    vec2 z = (vec2(-1.0*ratio, -1.0) + 2.0*p)*1.5;
    vec2 c = 0.5*o; 

    return iter(z, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    // https://mathcoloring.wordpress.com/2016/01/14/math-mistakes-driving-me-crazy/
    float Ax = 4.0;
    float Bx = 16.0;
    float Ay = 6.0;
    float By = 14.0;
    float t = iTime*0.3;
    float x = cos(t) - 0.5*cos(Ax*t) + 1.0/3.0 * cos(-Bx*t);
    float y = sin(t) - 0.5*sin(Ay*t) + 1.0/3.0 * sin(-By*t);

	float sn = mandel(fragCoord.xy/iResolution.y, vec2(x,y));
	//float sn = julia(fragCoord.xy/iResolution.y, vec2(x,y));
    fragColor = vec4(0.03*sn, 0.0, 0.0, 1.0);
}
