#define t iTime
#define r iResolution.xy
#define m iMouse.xy

vec2 nc (in vec2 uv) {
	return (uv / r) * 2. - 1.;
}

float random (in vec2 uv, in vec3 seed) {
	return fract(sin(dot(uv.xy, vec2(seed.x,seed.y))) * seed.z);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = nc(fragCoord.xy) * 10.0;
	vec2 ipos = floor(uv);

	vec3 color = vec3(
		random(ipos,vec3(12.843, 78.324, 252332.0 + t)),
		random(ipos,vec3(92.843, 18.324, 152332.0 + t)),
		random(ipos,vec3(22.843, 38.324, 452332.0 + t)));

	fragColor = vec4(color, 1.0);
}
