float map(vec3 p) {
    vec3 q = fract(p) * 2. - 1.;
    float z = sin(0.125 + p.x * 0.156 + sin(iTime));
  	z = min(z,0.5);
    z = mix(z,p.x,p.y);

    return length(q) - 0.25 - z;
}

float trace(vec3 ro, vec3 rd) {
	float t = 0.;
    vec3 p = vec3(0.);

    for(int i = 0; i < 32; i++) {
        t += map(p) * 0.5;
        p = ro + rd * t ;
    }

    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv *= 2.;
    uv -= 1.;

    uv.x *= iResolution.x / iResolution.y;


    vec3 rd = normalize(vec3(uv,1.));

	vec3 ro = vec3(0.,0.,iTime);
    float t = trace(ro,rd);
    // Output to screen
    fragColor = vec4(1. / ( 1. + t * t * 0.1));
}
