// thikness of border
float _thikness = 0.001;

int _coubSize = 20;

vec4 paperColor(in vec2 fragCoord) {
    int x = int(fragCoord.x);
    int y = int(fragCoord.y);
    if (x % _coubSize == 0 || y % _coubSize == 0) {
        return vec4(0.639, 0.78, 0.933, 1.0);
    }

    return vec4(1.0, 1.0, 1.0, 1.0);
}

bool isObject(in vec2 uv, in float thikness) {
    vec4 tex = texture(iChannel0, uv);

    vec4 beforeX = texture(iChannel0, vec2(uv.x - thikness/2.0, uv.y));
    vec4 beforeY = texture(iChannel0, vec2(uv.x, uv.y - thikness/2.0));

	if (abs(tex.r - beforeX.r) > 0.01 || abs(tex.r - beforeY.r) > 0.01) {
        return true;
    }

    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = paperColor(fragCoord);

	if (isObject(uv, _thikness)) {
        vec4 tex = texture(iChannel0, uv);

        float blue = tex.r * 0.737;
        if (blue < 0.5) {
            blue = 0.5;
        }

        fragColor = vec4(0.035, 0.2196, blue, 1.0);
    }
}
