void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  	vec2 xy;
    xy.x = ((fragCoord.x - iMouse.x)/ iResolution.y);
    xy.y = ((fragCoord.y - iMouse.y) / iResolution.y);


   	float taille = 3.;
    float dist = length(xy);
    float colByDist = (taille/dist);   

    if(dist > 0.02)
    {
        float colorR = cos(colByDist + 50.*iTime);
        float colorG = sin(colByDist + 20.*iTime);
        float colorB = cos(colByDist + 10.*iTime);
        fragColor = vec4(colorR, sin(4.*iTime)*colorG, cos(2.*iTime)*colorB ,1.); 
    }
    else
    {    
    	fragColor = vec4(0);
    }

}
