#define csize 25
#define segs 20.
#define PI 3.14159
#define R1D 275.
#define R2D 400.

float lineseg(vec2 u, vec2 s, vec2 e)
{
    vec2 l1 = u-s;
    vec2 l2 = (e-s)*(clamp( dot(l1,(e-s) ) /(dot(e-s,e-s)), 0., 1. ) );

    return 1.-smoothstep(0.,4.,length(l1-l2));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = 800.*(2.*fragCoord-iResolution.xy)/iResolution.x;

    float c = lineseg(uv,vec2(-csize,0),vec2(csize,0));
    c+= lineseg(uv,vec2(0,-csize),vec2(0,csize));

    float s = sin(PI/segs*2.), o = cos(PI/segs*2.);

    for(float i = 0.; i < segs; i++)
    {
        c += lineseg(uv,vec2(-R1D,-20),vec2(-R1D,20));
    	c += lineseg(uv,vec2(-R1D,20),vec2(-R1D-15.,35));
    	c += lineseg(uv,vec2(-R1D,-20),vec2(-R1D+15.,-35));
        uv *= mat2(o,s,-s,o);
    }

    for(float i = 0.; i < segs; i++)
    {
        c += lineseg(uv,vec2(-R2D,-40),vec2(-R2D,40));
        c += lineseg(uv,vec2(-R2D,40),vec2(-R2D+20.,55.));
        c += lineseg(uv,vec2(-R2D,-40),vec2(-R2D-20.,-55.));
		uv *= mat2(o,s,-s,o);
    }

    // Time varying pixel color
    vec3 col = vec3(c);

    // Output to screen
    fragColor = 1.-vec4(col,1.0);
}
