#define t iTime
#define r iResolution.xy

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / r;
	uv = uv * 2.0 - 1.0;
	float d = length(uv);
	float a = atan(uv.y, uv.x);
	float f = 2.0*cos(a*t*50.0);
	float p = 1.0 - smoothstep(f, f+0.02, d);
	vec3 color = vec3(p);
	fragColor = vec4(color, 1.0);
}
