void mainImage (out vec4 c, in vec2 f) {
	vec2	v = (f.xy / iResolution.y) * 2. - 1.,
       		q = v, 
        	o;

    c -= c; //mac

    o.x = v.x - .5;
    o.y = abs(v.y);

    for (float i = 0.; i < 18.; i++) {
        v = o;

        v.y = .12 / v.y;
		v.x *= v.y;
        v.y += iTime * 1.75;
        v.x += -.1 + sin(v.y / i / 4. + i) * i * 2.; 

        for (float j = .1 ; j < .3; j += .1) 
    		if ((v.x += j) > -.1  && v.x < .1) 
                c += max(-.7, 2.2 - pow((abs(v.x - .05)) * 64., 0.4) * 2.2);
    }

    c += fract(v.y) < .02 ? .2 : 0.;
    if (q.y > 0.) c = .3 - clamp(c, 0., 1.)  - q.y / 1.5;
}
