#define sides floor(iTime)+3.
#define apotheme 0.5
//apotheme

/*	USING SIDE LENGTH
#define sideLength 1.
#define apotheme ((sideLength)/tan(pi/(sides))/2.)
*/

/* USING RADIUS LENGTH
#define radiusLength 1.
#define apotheme ((radiusLength)*cos(pi/(sides)))
*/

/* USING PERIMETER
#define perimeter 2.
#define apotheme ((perimeter)/tan(pi/(sides))/2./(sides))
*/

/* USING AREA
#define area 2.
#define apotheme sqrt((area)/(sides)/tan(pi/(sides)))
*/

const float pi=3.141592653589793;

void mainImage( out vec4 o, in vec2 p){
    p=p/iResolution.xy*2.-1.;
    p.x*=iResolution.x/iResolution.y;

	float ang=atan(p.x,p.y);

    o=vec4(smoothstep(0.,4./iResolution.y,cos(ang-floor(ang/pi/2.*(sides))/(sides)*pi*2.-pi/(sides))*length(p)-(apotheme)));
	//o=mix(o,vec4(smoothstep(0.,0.01,length(p)-(apotheme))),0.3);
}

