void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    // There is probably 2 real lines in a 'soundcloud texture'
    // line 0. containing an FFT of a time frame
    // line 1. containing the corresponding waveform
    float v = texture(iChannel0, vec2(uv.x,0.)).x;
    if (uv.y > v) fragColor = vec4(0.);
    else          fragColor = vec4(0., v, v, 0.);
}
