void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

vec4 load(vec2 fc) {
    return texture(iChannel0, fc/iChannelResolution[0].xy);
}
vec4 load1(vec2 fc) {
    return texture(iChannel1, fc/iChannelResolution[1].xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int k = iFrame % 9;
    if (k == 0) {
    	fragColor = texture(iChannel0, fragCoord/iResolution.xy);
    } else if (k < 5) {
        float f = pow(2.0, float(k));
        float x = mod(fragCoord.x - 0.5, iResolution.x / f) * 2.0 + 0.5;
        float n = fragCoord.x * f / iResolution.x;
        vec4 c0 = load1(vec2(x, fragCoord.y));
        vec4 c1 = load1(vec2(x + 1.0, fragCoord.y));
        if (n < 1.0) {            
            fragColor = (c0 + c1) / 2.0;
        } else if (n < 2.0) {
            fragColor = (c0 - c1) / 2.0;
        } else {
            fragColor = load1(fragCoord);
        }
    } else {
        float f = pow(2.0, float(k - 5 + 1));
        float x = mod(fragCoord.y - 0.5, iResolution.y / f) * 2.0 + 0.5;
        float n = fragCoord.y * f / iResolution.y;
        vec4 c0 = load1(vec2(fragCoord.x, x));
        vec4 c1 = load1(vec2(fragCoord.x, x + 1.0));
        if (n < 1.0) {            
            fragColor = (c0 + c1) / 2.0;
        } else if (n < 2.0) {
            fragColor = (c0 - c1) / 2.0;
        } else {
            fragColor = load1(fragCoord);
        }
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    int k = iFrame % 9;  
    int n = (iFrame / (9 * 4)) % 9;
    if (k == n) {
        fragColor = texture(iChannel0, uv);
    } else {
    	fragColor = texture(iChannel1, uv);
    }
}
