void mainImage( out vec4 O, vec2 U )
{
   float t = mod(iTime,8.), 
         N = 3.+5.*min(t,8.-t),
         r = .5;                  // interior radius (ext = 1.)

    vec2 R = iResolution.xy;
    U = ( U+U - R ) / R.y;
    float a = atan(U.x,U.y), l = length(U),
          b = 3.14159/N,
        tb = tan(b),
       // s = sin(b); // if control by peak angle
          s = (r * tb)/(sqrt(1.+tb*tb)-r); // control by r
    a = mod(a,2.*b)-b;
    U = l * vec2(cos(a),sin(a)) / cos(b);
    U.y = abs(U.y);
    U.x -= 1.;
    O = vec4( l = -(s*U.x+U.y)/sqrt(1.+s*s) );

    //O = sin(30.*O);
    if (O.x<0.) O=vec4(1,0,0,0);
    O.b = .5*sin(100.*l);
}
