const float force = 4000.0;

const bool bDebug = false;

float length2(vec2 d){ return dot(d,d);}
float saturate(float i){ return clamp(i,0.0,1.0);}
float metaball(vec2 fragcoord, vec2 pos,float f)
{
    return f/length2((fragcoord - pos));
}
float noise(vec2 i)
{
    return 1.0 + sin(0.5*i.x + 1.0)*0.25 + cos(0.25*i.y*3.14)*cos(0.25*i.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 middle = iResolution.xy/2.0;
    float r = 90.0 + sin(iTime)*25.0;
    float b = metaball(fragCoord, vec2(middle.x + cos(iTime)*r,middle.y + sin(iTime)*r),force);

    b += metaball(fragCoord, vec2(middle.x, middle.y),force);

    b -= metaball(fragCoord,vec2(100,0),4.0*force);
    b -= metaball(fragCoord,middle + vec2(100,0),force*2.0);
	vec4 c = vec4(0);
    if( b < 0.8)
        c = vec4(1.0*abs(b*b*b),1.0*abs(b*b*b),abs(b)*0.2,1);
    else
        c = vec4(0.2*b,0.2*b,b,1.0);    

    fragColor = c;
    if(bDebug)
    	fragColor = vec4(abs(b),b,b,1.0);
    fragColor *= vec4(noise(fragCoord.xy));

}
