//Thanks Fabrice
#define tex(a,b) textureLod(a,b,0.)
#define C(c) U.x-=.5; T+= U.x<.0||U.x>1.||U.y<0.||U.y>1. ?vec4(0): tex( iChannel3, U/16. + fract( floor(vec2(c, 15.999-float(c/16))) / 16.))
#define initMsg vec4 T = vec4(0)
#define endMsg return length(T.yz)==0. ? 0. : T.x

// |
#define OR(a,b)  min(1.,a+b)
// &
#define AND(a,b) a*b
// ^
#define XOR(a,b) (a*(1.-b)+b*(1.-a))
// !
#define N		 1.-

float msg_and(vec2 U) { initMsg; C(65);C(78);C(68); endMsg; }
float msg_or (vec2 U) { initMsg; C(79);C(82); endMsg; }
float msg_xor(vec2 U) { initMsg; C(88);C(79);C(82); endMsg; }
float msg_inp(vec2 U) { initMsg; C(73);C(78);C(80);C(85);C(84); endMsg; }

void mainImage(out vec4 o, in vec2 c) {
    vec2 a = c.xy / iResolution.xy;
    vec2 v = fract(a), v2 = fract(2.*a);
    float a1 = step(.5,v.x), a2 = step(.5,v.y), i1 = step(.5,v2.x), i2 = step(.5,v2.y);
    o = step(.5, N OR(a1,a2))*		vec4(AND(i1,i2)+msg_and(v*15.-.1),0.,0.,1.) +
    	step(.5,a2- AND(a1,a2))*	vec4(msg_inp(v*15.-vec2(.1,8.))+(i1+i2)*.5) +
	    step(.5,a1- AND(a1,a2))*	vec4(0.,0.,XOR(i1,i2)+msg_xor(v*15.-vec2(7.,.1)),1.) +
    	step(.5, AND(a1,a2))*		vec4(0.,OR(i1,i2)+msg_or(v*15.-vec2(7.,8.)),0.,1.);
}
