#define mainImage(o,U)		\
vec2 u=U/iResolution.x-.5;	\
float L=length(u),t=iTime;	\
for(int i=0;i<3;i++)		\
    o[i]=.01/length(fract(.5+u+u/L*(sin(t+=.05)+1.)*sin(L*9.-t-t))-.5)/L

