void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv -= .5;

    float p = sqrt(dot(uv, uv)) * (sin(iTime * 20.) * 2. + 2.);

	float a = atan(uv.y, uv.x);

    float d = length(uv);
    d -= iTime * 1.;

    a += fract(iTime * 5.);

    vec4 c = texture(iChannel0, vec2(1., d));

    c += texture(iChannel0, vec2(1., a)) * fract(iTime*5.);
    c *= smoothstep(0., .5, p);

    fragColor = c;
}
