#define PI 3.1415926535897932384626433832795
#define half_PI 1.570796326794896619231321692

float max_dist = distance(vec2(0.5, 0.5), vec2(1.0, 1.0));
vec2 center = vec2(0.5, 0.5);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float dist = distance(uv, center) / max_dist;

    float a = 1.0 - abs(
        floor(uv.x + 0.5) -
        mod((atan(uv.y - 0.5, uv.x - 0.5) + half_PI) / PI, 1.0)
    );

	float c0 = texture(iChannel0, vec2(a, 0.25)).x;

    fragColor = vec4(1.0, 1.0 - dist, c0, 1.0) * (c0 / dist);
}
