void mainImage( out vec4 O, vec2 U )
{
   float t = mod(iTime,8.), 
         N = 3.+5.*min(t,8.-t);

    vec2 R = iResolution.xy;
    U = ( U+U - R ) / R.y;
    float a = atan(U.x,U.y), l = length(U),
          b = 3.14159/N;
    a = mod(a,2.*b)-b;
    O = vec4( l = 1. - l * cos(a) / cos(b) );
/** // variant :
    U = l * vec2(cos(a),sin(a));
    O = vec4( 1.-U.x/cos(b) );   // but if you simplify, it's same expression :-)
/**/

    //O = sin(30.*O);
    if (O.x<0.) O=vec4(1,0,0,0);
    O.b = .5*sin(100.*l);
}
