#define t iTime
#define r iResolution.xy
#define m iMouse.xy

vec2 nc (in vec2 uv) {
	return (uv / r) * 2. - 1.;
}

float random (in vec2 uv, in vec3 seed) {
	return fract(sin(dot(uv.xy, vec2(seed.x,seed.y))) * seed.z);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / r;
	vec2 uv2 = nc(fragCoord.xy) * 10.0;
	vec2 ipos = floor(uv2);

	vec3 color1 = vec3(uv.x, uv.y, abs(sin(t)));
	vec3 color2 = vec3(random(ipos, vec3(12.843, 78.324, 252332.0 + t)));
	vec3 color = mix(color1, color2, vec3(0.5, 0.5, 0.5));
	fragColor = vec4(color, 1.0);
}
