void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float a = uv.x;
    float b = uv.y;

    float c = (a * b + a * b) / (a + b);
    float d = (c + a - b * b) * c - a * b;


    vec2 m = iMouse.xy / iResolution.xy;
    for(int i = 0; i < 300; i++) {
    	a = a + c - b + m.x;
        b = b + c + d - m.y;

        if(c < d) {
            break;
        }


        c = (a * b + a * b) / (a + b);
        d = (c + a - b * b) * c - a * b;



        fragColor = vec4(c,d,a,1.);
    }
    // Output to screen

}
