#define zoom 2.
#define xInit 0.

#define S(v) smoothstep(pixels,0.,v)
float f(float x,vec2 mouse){
	return sin((x-mouse.x))+mouse.y;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
	uv.x*=iResolution.x/iResolution.y;
    uv*=zoom;
	vec2 mouse = iMouse.xy/iResolution.xy*2.-1.;
	mouse.x*=iResolution.x/iResolution.y;
	mouse*=zoom;

	float n=texture(iChannel0,vec2(0)).r;
	n-=f(n,mouse)*0.0002/(f(n+0.0001,mouse)-f(n-0.0001,mouse));

	if(iTime<1.)n=(xInit);

    float pixels=2./iResolution.y*(zoom);
    vec3 col = vec3(0,0,1)*smoothstep(pixels,0.,abs(f(uv.x,mouse)-uv.y));

    col+=S(abs(uv.x-n))*vec3(1,0,0);
	col+=0.3*S(abs(uv.x));
    col+=0.3*S(abs(uv.y));
	col+=0.2*S(abs(fract(abs(uv.x)-0.5)-0.5));
    col+=0.2*S(abs(fract(abs(uv.y)-0.5)-0.5));
    col+=0.1*S(abs(fract(abs(uv.x)*10.-0.5)-0.5)/10.);
    col+=0.1*S(abs(fract(abs(uv.y)*10.-0.5)-0.5)/10.);

    fragColor = vec4(vec3(col),1.0);
    fragColor+=PrintValue(uv,vec2(n,0.9*(zoom)),vec2(0.06)*(zoom),abs(n),2.,6.);

	fragColor.r=distance(fragCoord,vec2(0))<1.?n:fragColor.r;
}

float DigitBin( const int x )
{
    return x==0?480599.0:x==1?139810.0:x==2?476951.0:x==3?476999.0:x==4?350020.0:x==5?464711.0:x==6?464727.0:x==7?476228.0:x==8?481111.0:x==9?481095.0:0.0;
}

float PrintValue( const vec2 vStringCoords, const float fValue, const float fMaxDigits, const float fDecimalPlaces )
{
    if ((vStringCoords.y < 0.0) || (vStringCoords.y >= 1.0)) return 0.0;
	float fLog10Value = log2(abs(fValue)) / log2(10.0);
	float fBiggestIndex = max(floor(fLog10Value), 0.0);
	float fDigitIndex = fMaxDigits - floor(vStringCoords.x);
	float fCharBin = 0.0;
	if(fDigitIndex > (-fDecimalPlaces - 1.01)) {
		if(fDigitIndex > fBiggestIndex) {
			if((fValue < 0.0) && (fDigitIndex < (fBiggestIndex+1.5))) fCharBin = 1792.0;
		} else {		
			if(fDigitIndex == -1.0) {
				if(fDecimalPlaces > 0.0) fCharBin = 2.0;
			} else {
                float fReducedRangeValue = fValue;
                if(fDigitIndex < 0.0) { fReducedRangeValue = fract( fValue ); fDigitIndex += 1.0; }
				float fDigitValue = (abs(fReducedRangeValue / (pow(10.0, fDigitIndex))));
                fCharBin = DigitBin(int(floor(mod(fDigitValue, 10.0))));
			}
        }
	}
    return floor(mod((fCharBin / pow(2.0, floor(fract(vStringCoords.x) * 4.0) + (floor(vStringCoords.y * 5.0) * 4.0))), 2.0));
}


float PrintValue(const in vec2 fragCoord, const in vec2 vPixelCoords, const in vec2 vFontSize, const in float fValue, const in float fMaxDigits, const in float fDecimalPlaces)
{
    vec2 vStringCharCoords = (fragCoord.xy - vPixelCoords) / vFontSize;

    return PrintValue( vStringCharCoords, fValue, fMaxDigits, fDecimalPlaces );
}

