void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 O, in vec2 uv ){
    uv /= iResolution.xy;    
    vec4 old = texture(iChannel1, uv); // buffer           

    float d = .3 + .15 * sin(iTime * 3.);
    float t2 = iTime * 2. + sin(iTime * .03 + 2.);
    vec2 pen = vec2(
        d * cos(t2) * iResolution.y/iResolution.x,
    	d * sin(t2)
    ) + .5;

    float dis = distance(uv, pen) / .2;
    if(dis < 1.) {
      	O = old + pow(1.-dis, 20.) * .05;
    } else {
        O = old;
    }
    //clear
    //O = vec4(0.);
}
