float rand(float s) {

    //Thanks to Shane for the improved random function
    return fract(fract(s*157.)*43758.5453);
}
float rand(vec3 s) {

    //Thanks to Shane for the improved random function
    return fract(cos(dot(s, vec3(7, 157, 113)))*43758.5453);

    /* old
    return fract( (fract(s.x*32.924)*8. + fract(s.x*296.234) +
                 fract(s.y*32.924)*8. + fract(s.y*296.234) +
                 fract(s.z*32.924)*8. + fract(s.z*296.234))*98.397 );*/
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 uv = fragCoord/iResolution.xy;

    int frame = int(mod(float(iFrame),300.));
    float fframe = iTime;

    //random shifting/scaling
    vec2 rshift = vec2(rand(fframe-3.8234),rand(fframe+9.2348)),
         rscale = vec2(0.25+rand(fframe)*1.75,0.25+rand(fframe+1.2348)*1.75);
    if (frame == 0) {
        //start with random noise
        fragColor = texture(iChannel0, rshift+uv*rscale);
    } else {
        //shift towards target texture depending on difference & mask
        vec4 orig = texture(iChannel3, uv),targ;
        float rtex = rand(fframe+12.9923)*2.;
        if (rtex < 1.) {
            targ = texture(iChannel1, uv*rscale+rshift);
        } else {
            targ = texture(iChannel2, uv*rscale+rshift);
        }
        fragColor = mix(orig,targ,clamp(pow(1.0-length(orig.xyz-targ.xyz),5.)*0.1,0.0,1.0));
    }
}

