void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 sampleUV = uv;
    sampleUV /= 2.0;
    uv -= 0.5;
    uv *= 2.0;
     uv.x *= iResolution.x/iResolution.y;

    vec3 color = vec3(sin(iTime)+0.5,0.5,0.0);

    float fft = texture(iChannel0,sampleUV).r;
    color *= fft;
    float d = length(uv);

    color /= fract(d*sin(iTime+fft));
	fragColor = vec4(color,1.0);
}
